import axios from "axios";
import { showAlert } from "./alerts";

export const updateNameOrEmail = async (name, email) => {
  console.log("running update on username or email");
  try {
    const res = await axios({
      method: "PATCH",
      url: "http://localhost:2222/api/users/updateMe",
      data: {
        name: name,
        email: email,
      },
    });

    //? res.data is the data we send with our status.
    if (res.data.status === "success") {
      showAlert("success", "Updated information, reloading page");
      location.reload();
    }
  } catch (error) {
    showAlert("error", "Failed to change your details. Please try again");
  }
};

export const updatePassword = async (
  currentPassword,
  passwordOne,
  passwordTwo
) => {
  console.log("running update on username or email");
  try {
    const res = await axios({
      method: "PATCH",
      url: "http://localhost:2222/api/users/updateMyPassword",
      data: {
        currentPassword: currentPassword,
        newPassword: passwordOne,
        newPasswordConfirm: passwordTwo,
      },
    });

    //? res.data is the data we send with our status.
    if (res.data.status === "success") {
      showAlert("success", "Updated password, reloading page");
      location.reload();
    }
  } catch (error) {
    showAlert("error", "Failed to change your password. Please try again");
  }
};

export const resetPassword = async (token, passwordOne, passwordTwo) => {
  try {
    const res = await axios({
      method: "PATCH",
      url: "http://localhost:2222/api/users/resetPassword",
      data: {
        token: token,
        newPassword: passwordOne,
        newPasswordConfirm: passwordTwo,
      },
    });

    //? res.data is the data we send with our status.
    if (res.data.status === "success") {
      showAlert("success", "Updated password, please login again");
      window.setTimeout(() => {
        window.location = `/login`;
      }, 2000);
    }
  } catch (error) {
    showAlert("error", "Failed to change your password. Please try again");
  }
};

export const forgotPassword = async (email) => {
  try {
    const res = await axios({
      method: "POST",
      url: "http://localhost:2222/api/users/forgotPassword",
      data: {
        email: email,
      },
    });

    //? res.data is the data we send with our status.
    if (res.data.status === "success") {
      showAlert("success", "You should recieved you reset token soon!");
      window.setTimeout(() => {
        window.location = `/resetPassword`;
      }, 2000);
    }
  } catch (error) {
    showAlert("error", "Failed. Please try again");
  }
};
